using System;namespace Zeus.ErrorHandling{	/// <summary>	/// Summary description for ZeusScriptException.	/// </summary>	public class ZeusScriptException : ZeusException	{		private string source, text, description;		private int line, column, number;		private bool isTemplateScript;		public ZeusScriptException(Exception exception, string source, string text, string description, int number, int line, int column, bool isTemplateScript) : base(description, exception)		{			this.source = source;			this.text = text;			this.description = description;			this.number = number;			this.line = line;			this.column = column;			this.isTemplateScript = isTemplateScript;		}		public string ScriptErrorSource 		{			get 			{				return source;			}		}		public string ScriptErrorText 		{			get 			{				return text;			}		}		public string ScriptErrorDescription 		{			get 			{				return description;			}		}		public int ScriptErrorLine		{			get 			{				return line;			}		}		public int ScriptErrorColumn		{			get 			{				return column;			}		}		public int ScriptErrorNumber		{			get 			{				return number;			}		}		public bool IsTemplateScript		{			get 			{				return isTemplateScript;			}		}		public override string Message		{			get			{				return  "SCRIPTING ERROR:\r\n" +						"Source: " + source + "\r\n" +						"Text: " + text + "\r\n" +						"Description: " + description + "\r\n" +						"Number: " + number + "\r\n" +						"Line: " + line + "\r\n" +						"Column: " + column;			}		}	}}